export default new class _array {
  /**
   * @name pageCut-数组分页
   * @param { array } sourceArr - 源数据
   * @param { Number } [pageNum = 1] - 当前页码
   * @param { Number } [pageSize = 10] - 一页多少条
   */
  pageCut(sourceArr, pageNum = 1, pageSize = 10) {
    // no sourceArr 
    if (!sourceArr || !sourceArr.length) {
      return [];
    }
    // get beginIndex
    const BeginIndex = (pageNum - 1)* pageSize;
    const endIndex = pageNum * pageSize;
    return (endIndex > sourceArr.length)
            ? sourceArr.slice(BeginIndex, souceArr.length)
            : sourceArr.slice(BeginIndex, endIndex);
  }
}()